import logo from './logo.svg';
import './App.css';
import UserInput from './Component/UserInput';
import axios from 'axios';

function App() {
  let name = null;
  const apiCall =  async (cityName)=>{
  const responseCall = await axios('http://api.openweathermap.org/data/2.5/weather?q='+cityName+'&appid=94ab2eed0b08a6d02d4f181f574c3dcd')
    .then((response)=>{
      name = <h1>{response.data['name']}</h1>
    })
    .catch(err=>console.log(err))
    return responseCall;
}
console.log(name)
  return (
    <div className="App">
      <UserInput apiCall={apiCall}/>
      {name}      
    </div>
  );
}

export default App;
