class FormInput extends Component{
    render(){
        state={
            cityName:""
        }
        const submitHandler = (e)=>{
            e.preventDefault()
            console.log("working...")
        }
        return(
            <div classNameName="container">
               <div className="row"> 
                <div className="col-lg-4 mx-auto">
                <form onSubmit={submitHandler}>
  <div className="mb-3">
    <label htmlFor="cityname" className="form-label">Enter City Name</label>
    <input type="text" className="form-control" id="cityname" aria-describedby="cityHelp"></input>
    <div id="emailHelp" className="form-text">Enter City name to display weather update</div>
  </div>
  <button type="submit" className="btn btn-primary">Submit</button>
</form>
            </div>
            </div>
            </div>
        )
    }
}

export default FormInput;
