import logo from './logo.svg';
import './App.css';
import UserInput from './Component/UserInput';
import axios from 'axios';

function App() {
  let name;
  const apiCall =  async (cityName)=>{
  const responseCall = await axios('http://api.openweathermap.org/data/2.5/weather?q='+cityName+'&appid=94ab2eed0b08a6d02d4f181f574c3dcd')
    .then((response)=>{
      name = response.data['name']
      console.log(name)
    })
    .catch(err=>console.log(err))
    return responseCall;
}
  return (
    <div className="App">
      <UserInput apiCall={apiCall}/>      
    </div>
  );
}

export default App;
