import logo from './logo.svg';
import './App.css';
import React,{useEffect, useState} from 'react';
import UserInput from './Component/UserInput';
import axios from 'axios';

function App() {
  const [data, setData] = useState({
    temp:0,
    cityName:'',
    countryName:''
  })
  const apiCall =  async (cityName)=>{
  const responseCall = await axios('http://api.openweathermap.org/data/2.5/weather?q='+cityName+'&appid=94ab2eed0b08a6d02d4f181f574c3dcd')
    .then((response)=>{
      console.log(response.data['main']['temp'])
      setData({
        temp:response.data['main']['temp'],
        ...data
      })
      console.log(data)
    })
    .catch(err=>console.log(err))
    return responseCall;
}
  return (
    <div className="App">
      <UserInput apiCall={apiCall}/>
    </div>
  );
}

export default App;
