class App extends Component{
  state={
    temp:'TBS',
    feelsLike:'TBS',
    main:"TBS",
    description:"TBS",
    city:"TBS",
    country:"TBS"
  }
  render(){
    const apiCall = (cityName)=>{
    axios('http://api.openweathermap.org/data/2.5/weather?q='+cityName+'&appid=94ab2eed0b08a6d02d4f181f574c3dcd')
    .then((response)=>{
      this.setState({
        temp:response.data['main']['temp'],
        feelsLike:response.data['main']['feels_like'],
        main:response.data['weather'][0]['main'],
        description:response.data['weather'][0]['description'],
        city:response.data['name'],
        country:response.data['sys']['country']
      })
      console.log(this.state)
    })
    .catch(err=>console.log(err))
  }

    return(
          <div className="App">
      <UserInput apiCall={apiCall} temp={this.state.temp}/>
      <WeatherCard temp={this.state.temp} city={this.state.city} country={this.state.country} main={this.state.main}/>
    </div>
    )
  }
}

export default App;
