import React, { Component } from 'react';

class FormInput extends Component{
    render(){
        return(
            <div className="container">
                <form>
  <div class="mb-3">
    <label htmlFor="cityname" class="form-label">Enter City Name</label>
    <input type="text" class="form-control" id="cityname" aria-describedby="cityHelp"></input>
    <div id="emailHelp" class="form-text">Enter City name to display weather update</div>
  </div>
  <button type="submit" class="btn btn-primary">Submit</button>
</form>
            </div>
        )
    }
}

export default FormInput;
